mapper.select:
select 	a.*, b. posi_name
from 	employee a left outer join position b on (a.posi_cd = b.posi_cd)
where 	a.emp_no = #{empNo}

mapper.update:
	update	employee
	set		emp_nm = #{empNm}

    		, emp_tel = #{empTel}
    		, emp_mail = #{empMail}
    		, emp_zip = #{empZip}
    		, emp_add1 = #{empAdd1}
    		, emp_add2 = #{empAdd2}
	where	emp_no = #{empNo}
