mapper.select:
SELECT PA_NO , PA_NAME , PA_REG , PA_HP , PA_ZIP , PA_ADD1 , PA_ADD2 , PA_PIYN , PA_SEX FROM PATIENT
AND PA_NO = #{searchWord}
AND PA_NAME LIKE '%' || #{searchWord} || '%'

mapper.insert:
INSERT INTO CLINICAL_EXAMINATION (
    CEX_NO
    , CEX_CBC
    , CEX_ECG
    , CEX_UA
    , CEX_PFTS
    , CEX_DATE
    , CEX_NE
    , EMP_NO
    , CEX_STATUS
    , TRM_CD
    , DISEASE_CD
) VALUES (
    ('C'||LPAD(SEQ_CEX.NEXTVAL, 4, '0'))
	, #{cexCbc}
	, #{cexEcg}
	, #{cexUa}
	, #{cexPfts}
	, TO_CHAR(SYSDATE, 'YYYY-MM-DD')
	, #{cexNe}
	, 6
	, 'T'
	, #{trmCd}
	, #{diseaseCd}
)
