mapper.insert:
INSERT INTO CLINICAL_EXAMINATION (
    CEX_NO
    , CEX_STATUS
    , TRM_CD
    , DISEASE_CD
) VALUES (
    ('C'||LPAD(SEQ_CEX.NEXTVAL, 4, '0'))
	, 'F'
	, #{trmCd}
	, #{diseaseCd}
)

mapper.select:
SELECT
    COUNT(*)
FROM
    CLINICAL_EXAMINATION
WHERE TRM_CD = #{trmCd}
AND DISEASE_CD = #{diseaseCd}

/* kr.or.ddit.nurse.dao.OperationDAO.selectCexList */ SELECT C.TRM_CD , D.DISEASE_CD , A.PA_NO , A.PA_NAME , A.PA_SEX , A.PA_REG , E.CEX_NO FROM PATIENT A INNER JOIN RECEPTION B ON A.PA_NO = B.PA_NO INNER JOIN TRM_CHART C ON B.RCP_NO = C.RCP_NO INNER JOIN DIAGNOSIS_HISTORY D ON C.TRM_CD =D.TRM_CD INNER JOIN CLINICAL_EXAMINATION E ON D.TRM_CD =E.TRM_CD AND D.DISEASE_CD = E.DISEASE_CD WHERE E.CEX_STATUS = 'T'
AND A.PA_NO = #{searchWord}
AND A.PA_NAME LIKE '%' || #{searchWord} || '%'
ORDER BY E.CEX_DATE DESC

/* kr.or.ddit.nurse.dao.OperationDAO.selectOperationHistory */ SELECT E.op_no , D.PA_NAME , D.PA_REG , E.OP_END_TIME FROM TRM_CHART A INNER JOIN DIAGNOSIS_HISTORY B ON A.TRM_CD = B.TRM_CD INNER JOIN OPERATION_INFOMATION E ON B.DISEASE_CD = E.DISEASE_CD AND B.TRM_CD = E.TRM_CD LEFT OUTER JOIN CLINICAL_EXAMINATION F ON B.DISEASE_CD = F.DISEASE_CD AND B.TRM_CD = F.TRM_CD INNER JOIN RECEPTION C ON A.RCP_NO = C.RCP_NO INNER JOIN PATIENT D ON C.PA_NO = D.PA_NO WHERE E.OP_STATE = 'T'
AND D.PA_NO = #{searchWord}
AND D.PA_NAME LIKE '%' || #{searchWord} || '%'
ORDER BY OP_NO

mapper.update:
/* kr.or.ddit.nurse.dao.OperationDAO.updateCex */
	UPDATE clinical_examination
	SET
	    CEX_CBC = #{cexCbc}
	    , CEX_ECG = #{cexEcg}
	    , CEX_UA = #{cexUa}
	    , CEX_PFTS = #{cexPfts}
	    , CEX_DATE = SYSDATE
	    , CEX_NE = #{cexNe}
	    , CEX_STATUS = 'T'
	WHERE
	    CEX_NO = #{cexNo}

mapper.insert:
/* kr.or.ddit.nurse.dao.OperationDAO.InsertOperationJoin */
	INSERT INTO operation_join (
	    op_no,
	    emp_no,
	    op_nm
	) VALUES (
	    #{opNo,jdbcType=VARCHAR}
	    , #{empNo,jdbcType=NUMERIC}
	    , '집도의'
	)

mapper.update:
/* kr.or.ddit.nurse.dao.OperationDAO.updateOperation */
	UPDATE operation_infomation
	   SET 
	       OP_CD = #{opCd,jdbcType=VARCHAR}
	       , OP_COLOR_CD = #{opColorCd,jdbcType=VARCHAR}
	 WHERE OP_NO = #{opNo}

mapper.select:
/* kr.or.ddit.nurse.dao.OperationDAO.selectOperationJoin */
	SELECT COUNT(*)
	FROM OPERATION_INFOMATION A
	INNER JOIN OPERATION_JOIN B
	ON A.OP_NO = B.OP_NO
	WHERE B.OP_NO = #{opNo}

/* kr.or.ddit.nurse.dao.OperationDAO.checkOperationJoin */
	SELECT COUNT(*)
	FROM
	    operation_join
	where op_no = #{opNo}

mapper.update:
/* kr.or.ddit.nurse.dao.OperationDAO.updateOperationStarttime */

	UPDATE operation_infomation
    SET
        OP_START_TIME = #{opStartTime,jdbcType=TIMESTAMP}
	WHERE
	    op_no = #{opNo}

/* kr.or.ddit.nurse.dao.OperationDAO.updateOperationEndtime */

	UPDATE operation_infomation
    SET
        OP_END_TIME = #{opEndTime,jdbcType=TIMESTAMP}
        , OP_RECORD = #{opRecord,jdbcType=VARCHAR}
        , OP_STATE = 'T'
	WHERE
	    op_no = #{opNo}
