mapper.insert:
INSERT INTO NURSING_RECORD (
    NREC_NO
    , NREC_CONT
    , NREC_DATE
    , EMP_NO
    , PA_NO
    , HSPT_NO
) VALUES (
    SEQ_NREC.NEXTVAL
    , #{nrecCont}
    , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , 6
    , #{paNo}
    , #{hsptNo}
)

INSERT INTO vital (
    vt_no,
    vt_date,
    vt_tmp,
    vt_pls,
    vt_bp,
    vt_rp,
    vt_ne,
    emp_no,
    hspt_no
) VALUES (
    SEQ_VITAL.NEXTVAL
    , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , #{vtTmp,jdbcType=NUMERIC}
    , #{vtPls,jdbcType=NUMERIC}
    , #{vtBp,jdbcType=NUMERIC}
    , #{vtRp,jdbcType=NUMERIC}
    , #{vtNe,jdbcType=VARCHAR}
    , 6
    , #{hsptNo,jdbcType=NUMERIC}
)

mapper.select:
SELECT COUNT(*)
FROM INTAKE_OUTPUT
WHERE HSPT_NO = #{hsptNo}
  AND IO_DATE = #{ioDate}

mapper.insert:
INSERT INTO INTAKE_OUTPUT (
    IO_TOTAL
    , HSPT_NO
    , IO_DATE
) VALUES (
    0
    , #{hsptNo}
    , #{ioDate}
)

