INSERT INTO INTAKE (
     INTAKE_NO
   , INTAKE_CAPA
   , INTAKE_NE
   , INTAKE_CATE_CD
   , EMP_NO
   , IO_DATE
   , HSPT_NO
) VALUES (
    'IN'||LPAD(SEQ_INTAKE.NEXTVAL, 4,'0')
   , #{intakeCapa,jdbcType=VARCHAR}
   , #{intakeNe,jdbcType=VARCHAR}
   , #{intakeCateCd,jdbcType=VARCHAR}
   , 6
   , #{ioDate,jdbcType=DATE}
   , #{hsptNo,jdbcType=NUMERIC}
)
