mapper.update:
		UPDATE PHYSIOTHERAPY 
		SET PT_BED_STATE = #{ptBedState,jdbcType=VARCHAR},
    		RCP_NO = #{rcpNo,jdbcType=NUMERIC}
		WHERE PT_BED_CD=#{ptBedCd,jdbcType=VARCHAR}

mapper.select:
SELECT PA_NO, PA_NAME, PA_REG , PA_HP, PA_PRN , PA_ZIP , PA_ADD1, PA_ADD2, PA_PIYN , PA_SEX FROM PATIENT
AND PA_NO = #{searchWord}
AND PA_NAME LIKE '%' || #{searchWord} || '%'
