) VALUES (
   'STT'||LPAD(SEQ_STT_CD.nextval,4,'0')
   ,#{smsCont, jdbcType=VARCHAR}
   ,#{smsTitle, jdbcType=VARCHAR}
)

mapper.update:
		UPDATE statement
  		   SET	SMS_CONT = #{smsCont}
